import React, { useEffect, useState } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css'; // Import CSS của AOS

const App = () => {
  const [content, setContent] = useState([
    { id: 1, text: "Hello, world!", animation: "fade-up" },
    { id: 2, text: "This is an animated paragraph.", animation: "fade-left" }
  ]);

  useEffect(() => {
    AOS.init({
      offset: 200,
      delay: 0,
      duration: 1000,
      easing: 'ease-in-out',
    });

    AOS.refresh();
  }, [content]); // Gọi lại khi content thay đổi

  const addContent = () => {
    setContent([
      ...content,
      { id: content.length + 1, text: "New animated content", animation: "fade-right" }
    ]);
  };

  return (
    <div>
      {content.map((item) => (
        <h1 key={item.id} data-aos={item.animation}>{item.text}</h1>
      ))}
      <button onClick={addContent}>Add Content</button>
    </div>
  );
};

export default App;
